mapper.select:
select activity.*, department.name as departmentName
from activity
left join department on activity.department_id = department.id

     and id= #{id}
     and activity.name like concat('%', #{name}, '%')
     and img= #{img}
     and description like concat('%', #{description}, '%')
     and department_id= #{departmentId}
     and start= #{start}
     and end= #{end}
     and read_count= #{readCount}
     and content like concat('%', #{content}, '%')

order by id desc

select activity.* from activity_sign left join activity on activity_sign.activity_id = activity.id
and activity.name like concat('%', #{name}, '%') and activity_sign.user_id = #{userId}
order by id desc

select activity.* from likes left join activity on likes.fid = activity.id
and likes.module = '活动' and activity.name like concat('%', #{name}, '%') and likes.user_id = #{userId}
order by id desc

select activity.* from collect left join activity on collect.fid = activity.id
and collect.module = '活动' and activity.name like concat('%', #{name}, '%') and collect.user_id = #{userId}
order by id desc

select activity.* from comment left join activity on comment.fid = activity.id
and comment.module = '活动' and activity.name like concat('%', #{name}, '%') and comment.user_id = #{userId}
group by comment.fid , comment.module order by id desc
